void
KDirStatApp::slotFileAskOpenDir()
{
    slotStatusMsg( i18n( "Opening directory..." ) );

    {	
	KURL url=KFileDialog::getExistingDirectory( QString::null, this, i18n( "Open Directory..." ) );
	
	if( ! url.isEmpty() )
	{
	    openURL( url );
	}
    }
    slotStatusMsg( i18n( "Ready." ) );
}


void
KDirStatApp::slotFileOpenRecent( const KURL& url )
{
    slotStatusMsg( i18n( "Opening directory..." ) );

    if( ! url.isEmpty() )
    {
	openURL( url );
    }

    slotStatusMsg( i18n( "Ready." ) );
}


void
KDirStatApp::slotFileCloseDir()
{
    slotStatusMsg( i18n( "Closing directory..." ) );

    _view->clear();
    fileRefreshAll->setEnabled( false );
    close();

    slotStatusMsg( i18n( "Ready." ) );
}


void
KDirStatApp::slotRefreshAll()
{
    slotStatusMsg( i18n( "Refreshing directory tree..." ) );
    _view->refreshAll();
    slotStatusMsg( i18n( "Ready." ) );
}


void
KDirStatApp::slotRefreshSelected()
{
    if ( ! _view->selection() )
	return;
    
    slotStatusMsg( i18n( "Refreshing selected subtree..." ) );
    _view->refreshSelected();
    slotStatusMsg( i18n( "Ready." ) );
}


void
KDirStatApp::slotEditCopy()
{
    slotStatusMsg( i18n( "Copying selection to clipboard..." ) );

    if ( _view->selection() )
    {
	kapp->clipboard()->setText( _view->selection()->orig()->url() );
    }

    slotStatusMsg( i18n( "Ready." ) );
}


void
KDirStatApp::slotSelectionChanged( KFileInfo *selection )
{
    if ( selection )
    {
	editCopy->setEnabled( true );

	fileRefreshSelected->setEnabled( ! selection->isDotEntry() );
	slotStatusMsg( selection->url() );
    }
    else
    {
	editCopy->setEnabled( false );
	fileRefreshSelected->setEnabled( false );
	slotStatusMsg( "" );
    }
}


void
KDirStatApp::slotToggleToolBar()
{
    slotStatusMsg( i18n( "Toggling toolbar..." ) );

    // turn Toolbar on or off
    if( !viewToolBar->isChecked() )
    {
	toolBar( "mainToolBar" )->hide();
    }
    else
    {
	toolBar( "mainToolBar" )->show();
    }		

    slotStatusMsg( i18n( "Ready." ) );
}


void
KDirStatApp::slotToggleStatusBar()
{
    slotStatusMsg( i18n( "Toggle the statusbar..." ) );

    //turn Statusbar on or off
    if( !viewStatusBar->isChecked() )
    {
	statusBar()->hide();
    }
    else
    {
	statusBar()->show();
    }

    slotStatusMsg( i18n( "Ready." ) );
}


void
KDirStatApp::slotStatusMsg( const QString &text )
{
    // change status message permanently
    statusBar()->clear();
    statusBar()->changeItem( text, ID_STATUS_MSG );
}


void
KDirStatApp::slotContextMenu( KDirTreeViewItem *item, const QPoint &pos )
{
    (void) item;	// TODO - do something with this thing
    _treeViewContextMenu->popup( pos );
}


